import { useState, useEffect } from "react";
import "../App.css";

const TranscriptDisplay = ({ transcript }) => {
  const [selectedWords, setSelectedWords] = useState([]);

  const handleWordClick = (word) => {
    if (selectedWords.includes(word)) {
      // Deselect the word if it's already selected
      setSelectedWords(selectedWords.filter((w) => w !== word));
    } else {
      // Select the word
      setSelectedWords([...selectedWords, word]);
    }
  };

  return (
    <div>
      <p>
        {transcript.split(" ").map((word, index) => (
          <span
            key={index}
            onClick={() => handleWordClick(word)}
            style={{ cursor: "pointer", marginRight: "5px", color: selectedWord === word ? "blue" : "black" }}
          >
            {word}
          </span>
        ))}
      </p>
      {selectedWord && <p>Selected Word: <strong>{selectedWord}</strong></p>}
    </div>
  );
};

// Example usage
const App = () => {
  const [transcript, setTranscript] = useState("");

  useEffect(() => {
    // Fetch the transcript from the Flask backend
    fetch("http://127.0.0.1:5000/api/transcript")
      .then((response) => response.json())
      .then((data) => setTranscript(data.transcript))
      .catch((error) => console.error("Error fetching transcript:", error));
  }, []);

  if (!transcript) return <p>Loading transcript...</p>;

  return (<div className="transcript-container-scrollable">
    <TranscriptDisplay transcript={transcript}></TranscriptDisplay>
  </div>
  );
};

export default App;
