import { useState, useEffect } from "react";
import "../App.css";

const TranscriptDisplay = ({ transcript }) => {
  const [selectedWords, setSelectedWords] = useState([]);

  const handleWordClick = (word) => {
    if (selectedWords.includes(word)) {
      // Deselect the word if it's already selected
      setSelectedWords(selectedWords.filter((w) => w !== word));
    } else {
      // Select the word
      setSelectedWords([...selectedWords, word]);
    }
  };

  const clearAll = () => {
    setSelectedWords([]);
  }

  return (
    <div className="transcript-container-scrollable">
      <p>
        {transcript.split(" ").map((word, index) => (
          <span
            key={index}
            onClick={() => handleWordClick(word)}
            style={{
              cursor: "pointer",
              marginRight: "5px",
              color: selectedWords.includes(word) ? "blue" : "black",
              backgroundColor: selectedWords.includes(word) ? "#d0ebff" : "transparent",
              padding: "2px 4px",
              borderRadius: "4px",
            }}
          >
            {word}
          </span>
        ))}
      </p>
      <div style={{marginTop: "10px"}}>
      {selectedWords.length > 0 && (
        <p>
          Selected Words: <strong>{selectedWords.join(", ")}</strong>
          <button
              onClick={clearAll}
              style={{
                padding: "6px 12px",
                fontSize: "14px",
                backgroundColor: "#ff4d4d",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Clear All
            </button>
        </p>

      )}
      </div>
    </div>
  );
};

// Example usage
const App = () => {
  const [transcript, setTranscript] = useState("");

  useEffect(() => {
    // Fetch the transcript from the Flask backend
    fetch("http://127.0.0.1:5000/api/transcript")
      .then((response) => response.json())
      .then((data) => setTranscript(data.transcript))
      .catch((error) => console.error("Error fetching transcript:", error));
  }, []);

  if (!transcript) return <p>Loading transcript...</p>;

  return (<div className="transcript-container-scrollable">
    <TranscriptDisplay transcript={transcript}></TranscriptDisplay>
  </div>
  );
};

export default App;
